mapper.select:
SELECT BNO, WRITER, TITLE, CONTENT, POSTDATE, LASTMODIFIED, IP, HIT, IMAGE
  FROM BOARD
 WHERE BNO = #{bno}

mapper.update:
UPDATE BOARD
   SET HIT = HIT + 1
 WHERE BNO = #{bno}

UPDATE BOARD
   SET TITLE = #{param1}
     , CONTENT = #{param2}
     , IMAGE = #{param3}
     , LASTMODIFIED = SYSDATE
 WHERE BNO = #{param4}
